// templateRepositoryUsage displays the usage of the template-repository
// command and its subcommands.
func templateRepositoryUsage() {
	fmt.Fprintln(os.Stderr, `Template Repository APIs (/template/...)`)
	fmt.Fprintf(os.Stderr, "Usage:\n    %s [globalflags] template-repository COMMAND [flags]\n\n", os.Args[0])
	fmt.Fprintln(os.Stderr, "COMMAND:")
	fmt.Fprintln(os.Stderr, `    create: Create a new template.`)
	fmt.Fprintln(os.Stderr, `    submit: with action flag { forwardTo: "approval" | "draft" } and optional reviewComments. allow resubmission path with approver comments.`)
	fmt.Fprintln(os.Stderr, `    update: persist reviewer edits (metadata/clauses/semantics).`)
	fmt.Fprintln(os.Stderr, `    update-manage: update metadata or status.`)
	fmt.Fprintln(os.Stderr, `    search: perform filtered searches.`)
	fmt.Fprintln(os.Stderr, `    retrieve: load submitted template and history/provenance summary. fetch reviewed template with metadata, review history, and validation results. fetch all template entries for dashboard view.`)
	fmt.Fprintln(os.Stderr, `    retrieve-by-id: Retrieve a template by template id.`)
	fmt.Fprintln(os.Stderr, `    verify: run policy, schema, and semantic validations; return findings.`)
	fmt.Fprintln(os.Stderr, `    approve: mark template as approved, with optional decision notes.`)
	fmt.Fprintln(os.Stderr, `    reject: mark template as rejected, requiring reason field.`)
	fmt.Fprintln(os.Stderr, `    register: register new template into the repository.`)
	fmt.Fprintln(os.Stderr, `    archive: archive obsolete template.`)
	fmt.Fprintln(os.Stderr, `    audit: retrieve audit history of template actions.`)
	fmt.Fprintln(os.Stderr)
	fmt.Fprintln(os.Stderr, "Additional help:")
	fmt.Fprintf(os.Stderr, "    %s template-repository COMMAND --help\n", os.Args[0])
}
